    mess = ''
!Feasability
    CALL GETPARFEAS(distid, par, feas, err, mess)
    IF (err .GT. 0) THEN
      mess = 'GetPdf: '//TRIM(mess)
      RETURN
    ELSE IF (.NOT.feas) THEN
      RETURN
    ELSE
!Compute
      SELECT CASE  (distid) 
      CASE ('FlatPrior') 
        pdf = 0.0_mrk
      CASE ('Gaussian') 
        pdf = -(0.5_mrk*LOG(2._mrk*pi)) - LOG(par(2)) - 0.5_mrk*((x-par(&
&         1))/par(2))**2
      CASE ('LogNormal') 
        IF (x .LE. 0.0_mrk) THEN
          isnull = .true.
        ELSE
          pdf = -(0.5_mrk*LOG(2._mrk*pi)) - LOG(x*par(2)) - 0.5_mrk*((&
&           LOG(x)-par(1))/par(2))**2
        END IF
      CASE ('Exponential') 
        IF (x .LT. par(1)) THEN
          isnull = .true.
        ELSE
          pdf = -(1.0_mrk*LOG(par(2))) - (x-par(1))/par(2)
        END IF
      CASE ('Uniform') 
        IF (x .LT. par(1) .OR. x .GT. par(2)) THEN
          isnull = .true.
        ELSE
          pdf = -(1.0_mrk*LOG(par(2)-par(1)))
        END IF
      CASE ('Triangle') 
        IF (x .LT. par(2) .OR. x .GT. par(3)) THEN
          isnull = .true.
        ELSE IF (x .LE. par(1)) THEN
          pdf = LOG(2._mrk) + LOG(x - par(2)) - LOG(par(3) - par(2)) - &
&           LOG(par(1) - par(2))
        ELSE
          pdf = LOG(2._mrk) + LOG(par(3) - x) - LOG(par(3) - par(2)) - &
&           LOG(par(3) - par(1))
        END IF
      CASE DEFAULT
        err = 1
        mess = 'GetPdf:Fatal:Unavailable Dist'
      END SELECT
! Exponentiate if loga=.false.
      IF (.NOT.loga) THEN
        IF (isnull) THEN
          pdf = 0.0_mrk
        ELSE
          pdf = EXP(pdf)
        END IF
      END IF
    END IF
  END SUBROUTINE GETPDF

!  Differentiation of sigmafunk_apply in forward (tangent) mode (with options fixinterface noISIZE context OpenMP):
!   variations   of useful results: res
!   with respect to varying inputs: y par
!%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
  SUBROUTINE SIGMAFUNK_APPLY_D(funk, par, par_d, y, y_d, res, res_d, err&
&   , mess)
    IMPLICIT NONE
!^**********************************************************************
!^* Purpose: Apply the selected Sigmafunk
!^**********************************************************************
!^* Programmer: Ben Renard, Irstea Lyon
!^**********************************************************************
!^* Created: 29/04/2013, last modified: 05/08/2022, added 'Power'
!^**********************************************************************
!^* Comments:
!^**********************************************************************
!^* References:
!^**********************************************************************
!^* 2Do List:
!^**********************************************************************
!^* IN
!^*    1.funk, which function?? (e.g., 'Constant','Linear')
!^*    2.par, parameters of funk
!^*    3.Y, covariate of funk
!^* OUT
!^*    1.res, result
!^*    2.err, error code; <0:Warning, ==0:OK, >0: Error
!^*    3.mess, error message
!^**********************************************************************
    CHARACTER(len=*), INTENT(IN) :: funk
    REAL(mrk), INTENT(IN) :: par(:), y
    REAL(mrk), INTENT(IN) :: par_d(:), y_d
    REAL(mrk), INTENT(OUT) :: res
    REAL(mrk), INTENT(OUT) :: res_d
    INTEGER(mik), INTENT(OUT) :: err
    CHARACTER(len=*), INTENT(OUT) :: mess
! locals
    CHARACTER(len=250), PARAMETER :: procname='Sigmafunk_Apply'
    INTRINSIC TRIM
    INTRINSIC ABS
    INTRINSIC EXP
    REAL(mrk) :: abs0
    REAL(mrk) :: abs0_d
    REAL(mrk) :: abs1
    REAL(mrk) :: abs1_d
    REAL(mrk) :: abs2
    REAL(mrk) :: abs2_d
    REAL(mrk) :: abs3
    REAL(mrk) :: abs3_d
    REAL(mrk) :: pwr1
    REAL(mrk) :: pwr1_d
    REAL(mrk) :: arg1
    REAL(mrk) :: arg1_d
    REAL(mrk) :: temp
    err = 0
    res = undefrn
    SELECT CASE  (TRIM(funk)) 
    CASE ('Constant') 
      res_d = par_d(1)
      res = par(1)
    CASE ('Linear') 
      IF (y .GE. 0.) THEN
        abs0_d = y_d
        abs0 = y
      ELSE
        abs0_d = -y_d
        abs0 = -y
      END IF
      res_d = par_d(1) + abs0*par_d(2) + par(2)*abs0_d
      res = par(1) + par(2)*abs0
    CASE ('Power') 
      IF (y .GE. 0.) THEN
        abs1_d = y_d
        abs1 = y
      ELSE
        abs1_d = -y_d
        abs1 = -y
      END IF
      temp = abs1**par(3)
      IF (abs1 .LE. 0.0 .AND. (par(3) .EQ. 0.0 .OR. par(3) .NE. INT(par(&
&         3)))) THEN
        pwr1_d = 0.0_8
      ELSE IF (abs1 .LE. 0.0) THEN
        pwr1_d = par(3)*abs1**(par(3)-1)*abs1_d
      ELSE
        pwr1_d = par(3)*abs1**(par(3)-1)*abs1_d + temp*LOG(abs1)*par_d(3&
&         )
      END IF
      pwr1 = temp
      res_d = par_d(1) + pwr1*par_d(2) + par(2)*pwr1_d
      res = par(1) + par(2)*pwr1
    CASE ('Exponential') 
      IF (y .GE. 0.) THEN
        abs2_d = y_d
        abs2 = y
      ELSE
        abs2_d = -y_d
        abs2 = -y
      END IF
      temp = abs2/par(2)
      arg1_d = -((abs2_d-temp*par_d(2))/par(2))
      arg1 = -(temp**1)
      temp = -EXP(arg1) + 1._mrk
      res_d = par_d(1) + temp*(par_d(3)-par_d(1)) - (par(3)-par(1))*EXP(&
&       arg1)*arg1_d
      res = par(1) + (par(3)-par(1))*temp
    CASE ('Gaussian') 
      IF (y .GE. 0.) THEN
        abs3_d = y_d
        abs3 = y
      ELSE
        abs3_d = -y_d
        abs3 = -y
      END IF
      temp = abs3/par(2)
      arg1_d = -(2*temp*(abs3_d-temp*par_d(2))/par(2))
      arg1 = -(temp*temp)
      temp = -EXP(arg1) + 1._mrk
      res_d = par_d(1) + temp*(par_d(3)-par_d(1)) - (par(3)-par(1))*EXP(&
&       arg1)*arg1_d
      res = par(1) + (par(3)-par(1))*temp
    CASE DEFAULT
      err = 1
      res_d = 0.0_8
    END SELECT
  END SUBROUTINE SIGMAFUNK_APPLY_D

!  Differentiation of sigmafunk_apply in reverse (adjoint) mode (with options fixinterface noISIZE context OpenMP):
!   gradient     of useful results: res y par
!   with respect to varying inputs: y par
!%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
  SUBROUTINE SIGMAFUNK_APPLY_B(funk, par, par_b, y, y_b, res, res_b, err&
&   , mess)
    IMPLICIT NONE
!^**********************************************************************
!^* Purpose: Apply the selected Sigmafunk
!^**********************************************************************
!^* Programmer: Ben Renard, Irstea Lyon
!^**********************************************************************
!^* Created: 29/04/2013, last modified: 05/08/2022, added 'Power'
!^**********************************************************************
!^* Comments:
!^**********************************************************************
!^* References:
!^**********************************************************************
!^* 2Do List:
!^**********************************************************************
!^* IN
!^*    1.funk, which function?? (e.g., 'Constant','Linear')
!^*    2.par, parameters of funk
!^*    3.Y, covariate of funk
!^* OUT
!^*    1.res, result
!^*    2.err, error code; <0:Warning, ==0:OK, >0: Error
!^*    3.mess, error message
!^**********************************************************************
    CHARACTER(len=*), INTENT(IN) :: funk
    REAL(mrk), INTENT(IN) :: par(:), y
    REAL(mrk) :: par_b(:), y_b
    REAL(mrk) :: res
    REAL(mrk) :: res_b
    INTEGER(mik), INTENT(OUT) :: err
    CHARACTER(len=*), INTENT(OUT) :: mess
! locals
    CHARACTER(len=250), PARAMETER :: procname='Sigmafunk_Apply'
    INTRINSIC TRIM
    INTRINSIC ABS
    INTRINSIC EXP
    REAL(mrk) :: abs0
    REAL(mrk) :: abs0_b
    REAL(mrk) :: abs1
    REAL(mrk) :: abs1_b
    REAL(mrk) :: abs2
    REAL(mrk) :: abs2_b
    REAL(mrk) :: abs3
    REAL(mrk) :: abs3_b
    REAL(mrk) :: pwr1
    REAL(mrk) :: pwr1_b
    REAL(mrk) :: arg1
    REAL(mrk) :: arg1_b
    REAL(mrk) :: temp
    REAL(mrk) :: temp_b
    INTEGER :: branch
    SELECT CASE  (TRIM(funk)) 
    CASE ('Constant') 
      par_b(1) = par_b(1) + res_b
    CASE ('Linear') 
      IF (y .GE. 0.) THEN
        abs0 = y
        CALL PUSHCONTROL1B(0)
      ELSE
        abs0 = -y
        CALL PUSHCONTROL1B(1)
      END IF
      par_b(1) = par_b(1) + res_b
      par_b(2) = par_b(2) + abs0*res_b
      abs0_b = par(2)*res_b
      CALL POPCONTROL1B(branch)
      IF (branch .EQ. 0) THEN
        y_b = y_b + abs0_b
      ELSE
        y_b = y_b - abs0_b
      END IF
    CASE ('Power') 
      IF (y .GE. 0.) THEN
        abs1 = y
        CALL PUSHCONTROL1B(0)
      ELSE
        abs1 = -y
        CALL PUSHCONTROL1B(1)
      END IF
      pwr1 = abs1**par(3)
      pwr1 = abs1**par(3)
      par_b(1) = par_b(1) + res_b
      par_b(2) = par_b(2) + pwr1*res_b
      pwr1_b = par(2)*res_b
      IF (abs1 .LE. 0.0 .AND. (par(3) .EQ. 0.0 .OR. par(3) .NE. INT(par(&
&         3)))) THEN
        abs1_b = 0.0_8
      ELSE
        abs1_b = par(3)*abs1**(par(3)-1)*pwr1_b
      END IF
      IF (.NOT.abs1 .LE. 0.0) par_b(3) = par_b(3) + abs1**par(3)*LOG(&
&         abs1)*pwr1_b
      CALL POPCONTROL1B(branch)
      IF (branch .EQ. 0) THEN
        y_b = y_b + abs1_b
      ELSE
        y_b = y_b - abs1_b
      END IF
    CASE ('Exponential') 
      IF (y .GE. 0.) THEN
        abs2 = y
        CALL PUSHCONTROL1B(0)
      ELSE
        abs2 = -y
        CALL PUSHCONTROL1B(1)
      END IF
      arg1 = -((abs2/par(2))**1)
      arg1 = -((abs2/par(2))**1)
      temp_b = (1._mrk-EXP(arg1))*res_b
      par_b(1) = par_b(1) + res_b - temp_b
      arg1_b = -(EXP(arg1)*(par(3)-par(1))*res_b)
      par_b(3) = par_b(3) + temp_b
      temp = abs2/par(2)
      temp_b = -(arg1_b/par(2))
      abs2_b = temp_b
      par_b(2) = par_b(2) - temp*temp_b
      CALL POPCONTROL1B(branch)
      IF (branch .EQ. 0) THEN
        y_b = y_b + abs2_b
      ELSE
        y_b = y_b - abs2_b
      END IF
    CASE ('Gaussian') 
      IF (y .GE. 0.) THEN
        abs3 = y
        CALL PUSHCONTROL1B(0)
      ELSE
        abs3 = -y
        CALL PUSHCONTROL1B(1)
      END IF
      arg1 = -((abs3/par(2))**2)
      arg1 = -((abs3/par(2))**2)
      temp_b = (1._mrk-EXP(arg1))*res_b
      par_b(1) = par_b(1) + res_b - temp_b
      arg1_b = -(EXP(arg1)*(par(3)-par(1))*res_b)
      par_b(3) = par_b(3) + temp_b
      temp = abs3/par(2)
      temp_b = -(2*temp*arg1_b/par(2))
      abs3_b = temp_b
      par_b(2) = par_b(2) - temp*temp_b
      CALL POPCONTROL1B(branch)
      IF (branch .EQ. 0) THEN
        y_b = y_b + abs3_b
      ELSE
        y_b = y_b - abs3_b
      END IF
    END SELECT
  END SUBROUTINE SIGMAFUNK_APPLY_B

