    REAL(mrk) :: abs3
    REAL(mrk) :: pwr1
    REAL(mrk) :: arg1
    err = 0
    mess = ''
    res = undefrn
    SELECT CASE  (TRIM(funk)) 
    CASE ('Constant') 
      res = par(1)
    CASE ('Linear') 
      IF (y .GE. 0.) THEN
        abs0 = y
      ELSE
        abs0 = -y
      END IF
      res = par(1) + par(2)*abs0
    CASE ('Power') 
      IF (y .GE. 0.) THEN
        abs1 = y
      ELSE
        abs1 = -y
      END IF
      pwr1 = abs1**par(3)
      res = par(1) + par(2)*pwr1
    CASE ('Exponential') 
      IF (y .GE. 0.) THEN
        abs2 = y
      ELSE
        abs2 = -y
      END IF
      arg1 = -((abs2/par(2))**1)
      res = par(1) + (par(3)-par(1))*(1._mrk-EXP(arg1))
    CASE ('Gaussian') 
      IF (y .GE. 0.) THEN
        abs3 = y
      ELSE
        abs3 = -y
      END IF
      arg1 = -((abs3/par(2))**2)
      res = par(1) + (par(3)-par(1))*(1._mrk-EXP(arg1))
    CASE DEFAULT
      err = 1
      mess = TRIM(procname)//': unknown SigmaFunk'
    END SELECT
  END SUBROUTINE SIGMAFUNK_APPLY

