  FUNCTION ELT(p, q) RESULT (RES)
    IMPLICIT NONE
    REAL(sp), DIMENSION(:), INTENT(IN) :: p, q
    REAL(sp) :: res
    INTEGER :: n, i, imax_p, imax_q
    REAL(sp) :: max_p, max_q
    INTRINSIC SIZE
    n = SIZE(q)
    res = -99._sp
    max_p = -99._sp
    max_q = -99._sp
    imax_p = 0
    imax_q = 0
    DO i=1,n
      IF (p(i) .GT. max_p) THEN
        max_p = p(i)
        imax_p = i
      END IF
      IF (q(i) .GT. max_q) THEN
        max_q = q(i)
        imax_q = i
      END IF
    END DO
    IF (imax_p .GT. 0 .AND. imax_q .GT. 0) res = imax_q - imax_p
  END FUNCTION ELT

