    TYPE(OPTIONSDT), INTENT(IN) :: options
    INTEGER :: i, j, k
    REAL(sp) :: y, l, u
    LOGICAL, DIMENSION(mesh%nrow, mesh%ncol) :: ac_mask
    INTRINSIC SUM
    INTRINSIC TRIM
    ac_mask = mesh%active_cell(:, :) .EQ. 1
! RR parameters is first control kind
