&                          rr_parameters%values(:, :, i))
        parameters_b%rr_parameters%values(:, :, i) = 0.0_4
        DO k=setup%nd,1,-1
          CALL POPCONTROL1B(branch)
          IF (branch .NE. 0) THEN
            norm_desc_b = 0.0_4
            parameters_b%control%x(j-1) = parameters_b%control%x(j-1) + &
