    INTEGER :: ad_to1
    INTEGER :: ad_to2
    INTEGER :: ad_to3
    INTEGER :: ad_to4
    INTEGER :: ad_to5
    INTEGER :: ad_to6
    jobs_cmpt_values = 0._sp
    DO i=1,mesh%ng
! Cycle if gauge is equal to 0
      IF (options%cost%gauge(i) .EQ. 0) THEN
        CALL PUSHCONTROL1B(0)
      ELSE
        CALL PUSHREAL4ARRAY(qo, setup%ntime_step - options%cost%&
&                     end_warmup + 1)
