    TYPE(OPTIONSDT), INTENT(IN) :: options
    TYPE(RETURNSDT), INTENT(INOUT) :: returns
    INTEGER, INTENT(IN) :: time_step
    REAL(sp), DIMENSION(setup%neurons(2), setup%neurons(1)), INTENT(IN) &
